mapper.update:
UPDATE tb_staff
SET deleted = 1
WHERE id = #{id}

UPDATE tb_staff
SET dept_id = #{newId}
WHERE dept_id = #{oldId}

mapper.select:
SELECT s.id, s.real_name, s.age, s.gender, s.mobile, s.nick_name,s.dept_id, s.created_by, d.name AS deptName
FROM tb_staff AS s JOIN tb_department AS d ON s.dept_id = d.id

        s.deleted = 0

        AND real_name LIKE concat('%',#{realName},'%')


        AND age = #{age}


        AND gender = #{gender}


        AND mobile = #{mobile}


        AND nick_name LIKE concat('%',#{nickName},'%')


        AND dept_id = #{deptId}


        AND `name` LIKE concat('%',#{deptName},'%')
