mapper.update:
UPDATE tb_staff
SET deleted = 1
WHERE id = #{id}

mapper.select:
SELECT

FROM tb_staff

        deleted = 0

        AND real_name LIKE concat('%',#{realName},'%')


        AND age = #{age}


        AND gender = #{gender}


        AND mobile = #{mobile}


        AND nick_name LIKE concat('%',#{nickName},'%')
